package curated

import (
	stream "github.com/KromDaniel/regengo/stream"
	"regexp"
	"strings"
	"testing"
)

// Code generated by regengo
// Tests and benchmarks for pattern: (?P<user>[\w\.+-]+)@(?P<domain>[\w\.-]+)\.(?P<tld>[\w\.-]+)

var emailCaptureRegexp = regexp.MustCompile("(?P<user>[\\w\\.+-]+)@(?P<domain>[\\w\\.-]+)\\.(?P<tld>[\\w\\.-]+)")

var emailCaptureTestInputs = []string{"user@example.com", "john.doe+tag@subdomain.example.co.uk", "test@test.org"}

func TestEmailCaptureMatchString(t *testing.T) {
	for _, input := range emailCaptureTestInputs {
		expected := emailCaptureRegexp.MatchString(input)
		got := EmailCapture{}.MatchString(input)
		if got != expected {
			t.Errorf("MatchString(%q) = %v, want %v", input, got, expected)
		}
	}
}

func TestEmailCaptureMatchBytes(t *testing.T) {
	for _, input := range emailCaptureTestInputs {
		inputBytes := []byte(input)
		expected := emailCaptureRegexp.Match(inputBytes)
		got := EmailCapture{}.MatchBytes(inputBytes)
		if got != expected {
			t.Errorf("MatchBytes(%q) = %v, want %v", input, got, expected)
		}
	}
}

func TestEmailCaptureFindString(t *testing.T) {
	for _, input := range emailCaptureTestInputs {
		expectedMatches := emailCaptureRegexp.FindStringSubmatch(input)
		result, ok := EmailCapture{}.FindString(input)

		if len(expectedMatches) > 0 {
			if !ok {
				t.Errorf("FindString(%q) returned false, expected match", input)
				continue
			}
			if result.Match != expectedMatches[0] {
				t.Errorf("FindString(%q).Match = %q, want %q", input, result.Match, expectedMatches[0])
			}
			{
				if result.User != expectedMatches[1] {
					t.Errorf("FindString(%q).User = %q, want %q", input, result.User, expectedMatches[1])
				}
				if result.Domain != expectedMatches[2] {
					t.Errorf("FindString(%q).Domain = %q, want %q", input, result.Domain, expectedMatches[2])
				}
				if result.Tld != expectedMatches[3] {
					t.Errorf("FindString(%q).Tld = %q, want %q", input, result.Tld, expectedMatches[3])
				}
			}
		} else {
			if ok {
				t.Errorf("FindString(%q) returned true, expected no match", input)
			}
		}
	}
}

func TestEmailCaptureFindAllString(t *testing.T) {
	for _, input := range emailCaptureTestInputs {
		expectedAll := emailCaptureRegexp.FindAllStringSubmatch(input, -1)
		results := EmailCapture{}.FindAllString(input, -1)

		if len(results) != len(expectedAll) {
			t.Errorf("FindAllString(%q, -1) returned %d results, want %d", input, len(results), len(expectedAll))
			continue
		}
		for i := range results {
			if results[i].Match != expectedAll[i][0] {
				t.Errorf("FindAllString(%q, -1)[%d].Match = %q, want %q", input, i, results[i].Match, expectedAll[i][0])
			}
			{
				if results[i].User != expectedAll[i][1] {
					t.Errorf("FindAllString(%q, -1)[%d].User = %q, want %q", input, i, results[i].User, expectedAll[i][1])
				}
				if results[i].Domain != expectedAll[i][2] {
					t.Errorf("FindAllString(%q, -1)[%d].Domain = %q, want %q", input, i, results[i].Domain, expectedAll[i][2])
				}
				if results[i].Tld != expectedAll[i][3] {
					t.Errorf("FindAllString(%q, -1)[%d].Tld = %q, want %q", input, i, results[i].Tld, expectedAll[i][3])
				}
			}
		}
	}
}

