package curated

import (
	"fmt"
	"regexp"
	"testing"
)

// Code generated by regengo
// Tests and benchmarks for pattern: [\w\.+-]+@[\w\.-]+\.[\w\.-]+

var emailRegexp = regexp.MustCompile("[\\w\\.+-]+@[\\w\\.-]+\\.[\\w\\.-]+")

var emailTestInputs = []string{"aaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaa| me@myself.com"}

func TestEmailMatchString(t *testing.T) {
	for _, input := range emailTestInputs {
		expected := emailRegexp.MatchString(input)
		got := Email{}.MatchString(input)
		if got != expected {
			t.Errorf("MatchString(%q) = %v, want %v", input, got, expected)
		}
	}
}

func TestEmailMatchBytes(t *testing.T) {
	for _, input := range emailTestInputs {
		inputBytes := []byte(input)
		expected := emailRegexp.Match(inputBytes)
		got := Email{}.MatchBytes(inputBytes)
		if got != expected {
			t.Errorf("MatchBytes(%q) = %v, want %v", input, got, expected)
		}
	}
}

func BenchmarkEmail(b *testing.B) {
	inputs := emailTestInputs

	b.Run("Match", func(b *testing.B) {
		for i, input := range inputs {
			input := input
			b.Run(fmt.Sprintf("Input[%d]", i), func(b *testing.B) {
				b.Run("stdlib", func(b *testing.B) {
					b.ReportAllocs()
					for b.Loop() {
						_ = emailRegexp.MatchString(input)
					}
				})
				b.Run("regengo", func(b *testing.B) {
					b.ReportAllocs()
					for b.Loop() {
						_ = Email{}.MatchString(input)
					}
				})
			})
		}
	})

}
