package curated

import (
	"fmt"
	"regexp"
	"testing"
)

// Code generated by regengo
// Tests and benchmarks for pattern: (?:(?:a|b)|(?:k)+)*abcd

var greedyRegexp = regexp.MustCompile("(?:(?:a|b)|(?:k)+)*abcd")

var greedyTestInputs = []string{"aaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaabcd"}

func TestGreedyMatchString(t *testing.T) {
	for _, input := range greedyTestInputs {
		expected := greedyRegexp.MatchString(input)
		got := Greedy{}.MatchString(input)
		if got != expected {
			t.Errorf("MatchString(%q) = %v, want %v", input, got, expected)
		}
	}
}

func TestGreedyMatchBytes(t *testing.T) {
	for _, input := range greedyTestInputs {
		inputBytes := []byte(input)
		expected := greedyRegexp.Match(inputBytes)
		got := Greedy{}.MatchBytes(inputBytes)
		if got != expected {
			t.Errorf("MatchBytes(%q) = %v, want %v", input, got, expected)
		}
	}
}

func BenchmarkGreedy(b *testing.B) {
	inputs := greedyTestInputs

	b.Run("Match", func(b *testing.B) {
		for i, input := range inputs {
			input := input
			b.Run(fmt.Sprintf("Input[%d]", i), func(b *testing.B) {
				b.Run("stdlib", func(b *testing.B) {
					b.ReportAllocs()
					for b.Loop() {
						_ = greedyRegexp.MatchString(input)
					}
				})
				b.Run("regengo", func(b *testing.B) {
					b.ReportAllocs()
					for b.Loop() {
						_ = Greedy{}.MatchString(input)
					}
				})
			})
		}
	})

}
