package curated

import (
	"fmt"
	"regexp"
	"testing"
)

// Code generated by regengo
// Tests and benchmarks for pattern: (?:(?:a|b)|(?:k)+)+?abcd

var lazyRegexp = regexp.MustCompile("(?:(?:a|b)|(?:k)+)+?abcd")

var lazyTestInputs = []string{"aaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaabcd"}

func TestLazyMatchString(t *testing.T) {
	for _, input := range lazyTestInputs {
		expected := lazyRegexp.MatchString(input)
		got := Lazy{}.MatchString(input)
		if got != expected {
			t.Errorf("MatchString(%q) = %v, want %v", input, got, expected)
		}
	}
}

func TestLazyMatchBytes(t *testing.T) {
	for _, input := range lazyTestInputs {
		inputBytes := []byte(input)
		expected := lazyRegexp.Match(inputBytes)
		got := Lazy{}.MatchBytes(inputBytes)
		if got != expected {
			t.Errorf("MatchBytes(%q) = %v, want %v", input, got, expected)
		}
	}
}

func BenchmarkLazy(b *testing.B) {
	inputs := lazyTestInputs

	b.Run("Match", func(b *testing.B) {
		for i, input := range inputs {
			input := input
			b.Run(fmt.Sprintf("Input[%d]", i), func(b *testing.B) {
				b.Run("stdlib", func(b *testing.B) {
					b.ReportAllocs()
					for b.Loop() {
						_ = lazyRegexp.MatchString(input)
					}
				})
				b.Run("regengo", func(b *testing.B) {
					b.ReportAllocs()
					for b.Loop() {
						_ = Lazy{}.MatchString(input)
					}
				})
			})
		}
	})

}
