package curated

import (
	"fmt"
	stream "github.com/KromDaniel/regengo/stream"
	"regexp"
	"strings"
	"testing"
)

// Code generated by regengo
// Tests and benchmarks for pattern: (?P<year>\d{4})-(?P<month>\d{2})-(?P<day>\d{2})

var multiDateRegexp = regexp.MustCompile("(?P<year>\\d{4})-(?P<month>\\d{2})-(?P<day>\\d{2})")

var multiDateTestInputs = []string{"Events: 2024-01-15", "2024-06-20", "and 2024-12-25 are holidays", "No dates here", "Single date 2025-10-05 in text"}

func TestMultiDateMatchString(t *testing.T) {
	for _, input := range multiDateTestInputs {
		expected := multiDateRegexp.MatchString(input)
		got := MultiDate{}.MatchString(input)
		if got != expected {
			t.Errorf("MatchString(%q) = %v, want %v", input, got, expected)
		}
	}
}

func TestMultiDateMatchBytes(t *testing.T) {
	for _, input := range multiDateTestInputs {
		inputBytes := []byte(input)
		expected := multiDateRegexp.Match(inputBytes)
		got := MultiDate{}.MatchBytes(inputBytes)
		if got != expected {
			t.Errorf("MatchBytes(%q) = %v, want %v", input, got, expected)
		}
	}
}

func TestMultiDateFindString(t *testing.T) {
	for _, input := range multiDateTestInputs {
		expectedMatches := multiDateRegexp.FindStringSubmatch(input)
		result, ok := MultiDate{}.FindString(input)

		if len(expectedMatches) > 0 {
			if !ok {
				t.Errorf("FindString(%q) returned false, expected match", input)
				continue
			}
			if result.Match != expectedMatches[0] {
				t.Errorf("FindString(%q).Match = %q, want %q", input, result.Match, expectedMatches[0])
			}
			{
				if result.Year != expectedMatches[1] {
					t.Errorf("FindString(%q).Year = %q, want %q", input, result.Year, expectedMatches[1])
				}
				if result.Month != expectedMatches[2] {
					t.Errorf("FindString(%q).Month = %q, want %q", input, result.Month, expectedMatches[2])
				}
				if result.Day != expectedMatches[3] {
					t.Errorf("FindString(%q).Day = %q, want %q", input, result.Day, expectedMatches[3])
				}
			}
		} else {
			if ok {
				t.Errorf("FindString(%q) returned true, expected no match", input)
			}
		}
	}
}

func TestMultiDateFindAllString(t *testing.T) {
	for _, input := range multiDateTestInputs {
		expectedAll := multiDateRegexp.FindAllStringSubmatch(input, -1)
		results := MultiDate{}.FindAllString(input, -1)

		if len(results) != len(expectedAll) {
			t.Errorf("FindAllString(%q, -1) returned %d results, want %d", input, len(results), len(expectedAll))
			continue
		}
		for i := range results {
			if results[i].Match != expectedAll[i][0] {
				t.Errorf("FindAllString(%q, -1)[%d].Match = %q, want %q", input, i, results[i].Match, expectedAll[i][0])
			}
			{
				if results[i].Year != expectedAll[i][1] {
					t.Errorf("FindAllString(%q, -1)[%d].Year = %q, want %q", input, i, results[i].Year, expectedAll[i][1])
				}
				if results[i].Month != expectedAll[i][2] {
					t.Errorf("FindAllString(%q, -1)[%d].Month = %q, want %q", input, i, results[i].Month, expectedAll[i][2])
				}
				if results[i].Day != expectedAll[i][3] {
					t.Errorf("FindAllString(%q, -1)[%d].Day = %q, want %q", input, i, results[i].Day, expectedAll[i][3])
				}
			}
		}
	}
}

func BenchmarkMultiDate(b *testing.B) {
	inputs := multiDateTestInputs

	b.Run("Match", func(b *testing.B) {
		for i, input := range inputs {
			input := input
			b.Run(fmt.Sprintf("Input[%d]", i), func(b *testing.B) {
				b.Run("stdlib", func(b *testing.B) {
					b.ReportAllocs()
					for b.Loop() {
						_ = multiDateRegexp.MatchString(input)
					}
				})
				b.Run("regengo", func(b *testing.B) {
					b.ReportAllocs()
					for b.Loop() {
						_ = MultiDate{}.MatchString(input)
					}
				})
			})
		}
	})

	b.Run("FindFirst", func(b *testing.B) {
		for i, input := range inputs {
			input := input
			b.Run(fmt.Sprintf("Input[%d]", i), func(b *testing.B) {
				b.Run("stdlib", func(b *testing.B) {
					b.ReportAllocs()
					for b.Loop() {
						_ = multiDateRegexp.FindStringSubmatch(input)
					}
				})
				b.Run("regengo", func(b *testing.B) {
					b.ReportAllocs()
					for b.Loop() {
						_, _ = MultiDate{}.FindString(input)
					}
				})
				b.Run("regengo_reuse", func(b *testing.B) {
					b.ReportAllocs()
					var result *MultiDateResult
					for b.Loop() {
						result, _ = MultiDate{}.FindStringReuse(input, result)
					}
				})
			})
		}
	})

	b.Run("FindAll", func(b *testing.B) {
		for i, input := range inputs {
			input := input
			b.Run(fmt.Sprintf("Input[%d]", i), func(b *testing.B) {
				b.Run("stdlib", func(b *testing.B) {
					b.ReportAllocs()
					for b.Loop() {
						_ = multiDateRegexp.FindAllStringSubmatch(input, -1)
					}
				})
				b.Run("regengo", func(b *testing.B) {
					b.ReportAllocs()
					for b.Loop() {
						_ = MultiDate{}.FindAllString(input, -1)
					}
				})
				b.Run("regengo_append", func(b *testing.B) {
					b.ReportAllocs()
					results := make([]*MultiDateResult, 0, 100)
					for b.Loop() {
						results = MultiDate{}.FindAllStringAppend(input, -1, results[:0])
					}
				})
			})
		}
	})

}

func TestMultiDateFindReader(t *testing.T) {
	for _, input := range multiDateTestInputs {
		// Get expected matches from stdlib
		expectedAll := multiDateRegexp.FindAllString(input, -1)

		// Get matches from streaming API
		var streamMatches []string
		err := MultiDate{}.FindReader(strings.NewReader(input), stream.Config{}, func(m stream.Match[*MultiDateBytesResult]) bool {
			streamMatches = append(streamMatches, string(m.Result.Match))
			return true
		})
		if err != nil {
			t.Errorf("FindReader(%q) error: %v", input, err)
			continue
		}

		// Compare results
		if len(streamMatches) != len(expectedAll) {
			t.Errorf("FindReader(%q) got %d matches, want %d", input, len(streamMatches), len(expectedAll))
			continue
		}
		for i := range streamMatches {
			if streamMatches[i] != expectedAll[i] {
				t.Errorf("FindReader(%q)[%d] = %q, want %q", input, i, streamMatches[i], expectedAll[i])
			}
		}
	}
}

func TestMultiDateFindReaderCount(t *testing.T) {
	for _, input := range multiDateTestInputs {
		expectedAll := multiDateRegexp.FindAllString(input, -1)
		expected := int64(len(expectedAll))

		count, err := MultiDate{}.FindReaderCount(strings.NewReader(input), stream.Config{})
		if err != nil {
			t.Errorf("FindReaderCount(%q) error: %v", input, err)
			continue
		}
		if count != expected {
			t.Errorf("FindReaderCount(%q) = %d, want %d", input, count, expected)
		}
	}
}

func TestMultiDateFindReaderFirst(t *testing.T) {
	for _, input := range multiDateTestInputs {
		expected := multiDateRegexp.FindString(input)

		result, _, err := MultiDate{}.FindReaderFirst(strings.NewReader(input), stream.Config{})
		if err != nil {
			t.Errorf("FindReaderFirst(%q) error: %v", input, err)
			continue
		}

		if expected == "" {
			if result != nil {
				t.Errorf("FindReaderFirst(%q) = %q, want no match", input, string(result.Match))
			}
		} else {
			if result == nil {
				t.Errorf("FindReaderFirst(%q) = nil, want %q", input, expected)
			} else if string(result.Match) != expected {
				t.Errorf("FindReaderFirst(%q) = %q, want %q", input, string(result.Match), expected)
			}
		}
	}
}

func TestMultiDateMatchLengthInfo(t *testing.T) {
	minLen, maxLen := MultiDate{}.MatchLengthInfo()
	// Just verify we get sensible values
	if minLen < 0 {
		t.Errorf("MinMatchLen = %d, expected >= 0", minLen)
	}
	if maxLen != -1 && maxLen < minLen {
		t.Errorf("MaxMatchLen = %d < MinMatchLen = %d", maxLen, minLen)
	}
}
