func TestReplaceDateMatchString(t *testing.T) {
	for _, input := range replaceDateTestInputs {
		expected := replaceDateRegexp.MatchString(input)
		got := ReplaceDate{}.MatchString(input)
		if got != expected {
			t.Errorf("MatchString(%q) = %v, want %v", input, got, expected)
		}
	}
}

func TestReplaceDateMatchBytes(t *testing.T) {
	for _, input := range replaceDateTestInputs {
		inputBytes := []byte(input)
		expected := replaceDateRegexp.Match(inputBytes)
		got := ReplaceDate{}.MatchBytes(inputBytes)
		if got != expected {
			t.Errorf("MatchBytes(%q) = %v, want %v", input, got, expected)
		}
	}
}

func TestReplaceDateFindString(t *testing.T) {
	for _, input := range replaceDateTestInputs {
		expectedMatches := replaceDateRegexp.FindStringSubmatch(input)
		result, ok := ReplaceDate{}.FindString(input)

		if len(expectedMatches) > 0 {
			if !ok {
				t.Errorf("FindString(%q) returned false, expected match", input)
				continue
			}
			if result.Match != expectedMatches[0] {
				t.Errorf("FindString(%q).Match = %q, want %q", input, result.Match, expectedMatches[0])
			}
			{
				if result.Year != expectedMatches[1] {
					t.Errorf("FindString(%q).Year = %q, want %q", input, result.Year, expectedMatches[1])
				}
				if result.Month != expectedMatches[2] {
					t.Errorf("FindString(%q).Month = %q, want %q", input, result.Month, expectedMatches[2])
				}
				if result.Day != expectedMatches[3] {
					t.Errorf("FindString(%q).Day = %q, want %q", input, result.Day, expectedMatches[3])
				}
			}
		} else {
			if ok {
				t.Errorf("FindString(%q) returned true, expected no match", input)
			}
		}
	}
}

func TestReplaceDateFindAllString(t *testing.T) {
	for _, input := range replaceDateTestInputs {
		expectedAll := replaceDateRegexp.FindAllStringSubmatch(input, -1)
		results := ReplaceDate{}.FindAllString(input, -1)

		if len(results) != len(expectedAll) {
			t.Errorf("FindAllString(%q, -1) returned %d results, want %d", input, len(results), len(expectedAll))
			continue
		}
		for i := range results {
			if results[i].Match != expectedAll[i][0] {
				t.Errorf("FindAllString(%q, -1)[%d].Match = %q, want %q", input, i, results[i].Match, expectedAll[i][0])
			}
			{
				if results[i].Year != expectedAll[i][1] {
					t.Errorf("FindAllString(%q, -1)[%d].Year = %q, want %q", input, i, results[i].Year, expectedAll[i][1])
				}
				if results[i].Month != expectedAll[i][2] {
					t.Errorf("FindAllString(%q, -1)[%d].Month = %q, want %q", input, i, results[i].Month, expectedAll[i][2])
				}
				if results[i].Day != expectedAll[i][3] {
					t.Errorf("FindAllString(%q, -1)[%d].Day = %q, want %q", input, i, results[i].Day, expectedAll[i][3])
				}
			}
		}
	}
}

func BenchmarkReplaceDate(b *testing.B) {
	inputs := replaceDateTestInputs

	b.Run("Match", func(b *testing.B) {
		for i, input := range inputs {
			input := input
			b.Run(fmt.Sprintf("Input[%d]", i), func(b *testing.B) {
				b.Run("stdlib", func(b *testing.B) {
					b.ReportAllocs()
					for b.Loop() {
						_ = replaceDateRegexp.MatchString(input)
					}
				})
				b.Run("regengo", func(b *testing.B) {
					b.ReportAllocs()
					for b.Loop() {
						_ = ReplaceDate{}.MatchString(input)
					}
				})
			})
		}
	})

	b.Run("FindFirst", func(b *testing.B) {
		for i, input := range inputs {
			input := input
			b.Run(fmt.Sprintf("Input[%d]", i), func(b *testing.B) {
				b.Run("stdlib", func(b *testing.B) {
					b.ReportAllocs()
					for b.Loop() {
						_ = replaceDateRegexp.FindStringSubmatch(input)
					}
				})
				b.Run("regengo", func(b *testing.B) {
					b.ReportAllocs()
					for b.Loop() {
						_, _ = ReplaceDate{}.FindString(input)
					}
				})
				b.Run("regengo_reuse", func(b *testing.B) {
					b.ReportAllocs()
					var result *ReplaceDateResult
					for b.Loop() {
						result, _ = ReplaceDate{}.FindStringReuse(input, result)
					}
				})
			})
		}
	})

	b.Run("FindAll", func(b *testing.B) {
		for i, input := range inputs {
			input := input
			b.Run(fmt.Sprintf("Input[%d]", i), func(b *testing.B) {
				b.Run("stdlib", func(b *testing.B) {
					b.ReportAllocs()
					for b.Loop() {
						_ = replaceDateRegexp.FindAllStringSubmatch(input, -1)
					}
				})
				b.Run("regengo", func(b *testing.B) {
					b.ReportAllocs()
					for b.Loop() {
						_ = ReplaceDate{}.FindAllString(input, -1)
					}
				})
				b.Run("regengo_append", func(b *testing.B) {
					b.ReportAllocs()
					results := make([]*ReplaceDateResult, 0, 100)
					for b.Loop() {
						results = ReplaceDate{}.FindAllStringAppend(input, -1, results[:0])
					}
				})
			})
		}
	})

	b.Run("Replace", func(b *testing.B) {
		for j, template := range replaceDateTestReplacers {
			template := template
			stdlibTemplate := convertToStdlibTemplateReplaceDate(template)
			b.Run(fmt.Sprintf("Template[%d]", j), func(b *testing.B) {
				for i, input := range inputs {
					input := input
					inputBytes := []byte(input)
					j := j
					b.Run(fmt.Sprintf("Input[%d]", i), func(b *testing.B) {
						b.Run("stdlib", func(b *testing.B) {
							b.ReportAllocs()
							for b.Loop() {
								_ = replaceDateRegexp.ReplaceAllString(input, stdlibTemplate)
							}
						})
						b.Run("regengo_runtime", func(b *testing.B) {
							b.ReportAllocs()
							for b.Loop() {
								_ = ReplaceDate{}.ReplaceAllString(input, template)
							}
						})
						b.Run("regengo", func(b *testing.B) {
							b.ReportAllocs()
							for b.Loop() {
								switch j {
								case 0:
									_ = ReplaceDate{}.ReplaceAllString0(input)
								case 1:
									_ = ReplaceDate{}.ReplaceAllString1(input)
								case 2:
									_ = ReplaceDate{}.ReplaceAllString2(input)
								}
							}
						})
						b.Run("regengo_append", func(b *testing.B) {
							b.ReportAllocs()
							buf := make([]byte, 0, 4096)
							for b.Loop() {
								switch j {
								case 0:
									buf = ReplaceDate{}.ReplaceAllBytesAppend0(inputBytes, buf[:0])
								case 1:
									buf = ReplaceDate{}.ReplaceAllBytesAppend1(inputBytes, buf[:0])
								case 2:
									buf = ReplaceDate{}.ReplaceAllBytesAppend2(inputBytes, buf[:0])
								}
							}
						})
					})
				}
			})
		}
	})

}

func TestReplaceDateFindReader(t *testing.T) {
	for _, input := range replaceDateTestInputs {
		// Get expected matches from stdlib
		expectedAll := replaceDateRegexp.FindAllString(input, -1)

		// Get matches from streaming API
		var streamMatches []string
		err := ReplaceDate{}.FindReader(strings.NewReader(input), stream.Config{}, func(m stream.Match[*ReplaceDateBytesResult]) bool {
			streamMatches = append(streamMatches, string(m.Result.Match))
			return true
		})
		if err != nil {
			t.Errorf("FindReader(%q) error: %v", input, err)
			continue
		}

		// Compare results
		if len(streamMatches) != len(expectedAll) {
			t.Errorf("FindReader(%q) got %d matches, want %d", input, len(streamMatches), len(expectedAll))
			continue
		}
		for i := range streamMatches {
			if streamMatches[i] != expectedAll[i] {
				t.Errorf("FindReader(%q)[%d] = %q, want %q", input, i, streamMatches[i], expectedAll[i])
			}
		}
	}
}

