// DefaultMaxLeftover returns the recommended MaxLeftover value for streaming.
// For bounded patterns, this is 10 * MaxMatchLen.
// For unbounded patterns, this returns 1MB as a safety limit.
func (TDFANestedWord) DefaultMaxLeftover() int {
	return 1048576
}

// FindReader streams matches from an io.Reader.
//
// The onMatch callback is called for each match found. Return false to stop
// processing early. The StreamMatch.Result points into an internal buffer
// and is only valid during the callback - copy if needed.
//
// Returns nil on success (including early termination via callback).
// Returns error on read failure.
func (TDFANestedWord) FindReader(r io.Reader, cfg stream.Config, onMatch func(stream.Match[*TDFANestedWordBytesResult]) bool) error {
	if err := cfg.Validate(65536); err != nil {
		return err
	}
	cfg = cfg.ApplyDefaults(65536, 1048576)

	buf := make([]byte, cfg.BufferSize)
	leftover := 0
	streamOffset := int64(0)
	chunkIndex := 0

	reuseResult := &TDFANestedWordBytesResult{}

	for {
		n, err := r.Read(buf[leftover:])
		if n == 0 && err != nil {
			if err == io.EOF {
				// Process any remaining data in leftover
				if leftover > 0 {
					chunk := buf[:leftover]
					searchPos := 0
					for searchPos < len(chunk) {
						result, ok := TDFANestedWord{}.FindBytesReuse(chunk[searchPos:], reuseResult)
						if !ok {
							break
						}
						matchIdx := bytes.Index(chunk[searchPos:], result.Match)
						if matchIdx < 0 {
							break
						}
						matchStart := searchPos + matchIdx

						m := stream.Match[*TDFANestedWordBytesResult]{
							ChunkIndex:   chunkIndex,
							Result:       result,
							StreamOffset: streamOffset + int64(matchStart),
						}
						if !onMatch(m) {
							return nil
						}
						if len(result.Match) > 0 {
							searchPos = matchStart + len(result.Match)
						} else {
							searchPos++
						}
					}
				}
				return nil
			}
			return err
		}

		dataLen := leftover + n
		chunk := buf[:dataLen]
		isFull := n == cfg.BufferSize-leftover

		searchPos := 0
		committed := 0
		for searchPos < len(chunk) {
			result, ok := TDFANestedWord{}.FindBytesReuse(chunk[searchPos:], reuseResult)
			if !ok {
				break
			}
			matchIdx := bytes.Index(chunk[searchPos:], result.Match)
			if matchIdx < 0 {
				break
			}
			matchStart := searchPos + matchIdx
			matchEnd := matchStart + len(result.Match)

			if isFull && matchEnd > dataLen-cfg.MaxLeftover {
				// Match too close to boundary, defer to next chunk
				break
			}

			m := stream.Match[*TDFANestedWordBytesResult]{
				ChunkIndex:   chunkIndex,
				Result:       result,
				StreamOffset: streamOffset + int64(matchStart),
			}
			if !onMatch(m) {
				return nil
			}
			committed = matchEnd
			if len(result.Match) > 0 {
				searchPos = matchEnd
			} else {
				searchPos++
			}
		}

		// Prepare leftover for next iteration
		if isFull {
			// Buffer was full, need to keep leftover
			keepFrom := dataLen - cfg.MaxLeftover
			if keepFrom < committed {
				keepFrom = committed
			}
			leftover = dataLen - keepFrom
			streamOffset += int64(keepFrom)
			copy(buf[:leftover], buf[keepFrom:dataLen])
		} else {
			// Reached EOF or short read, no more data
			leftover = 0
		}
		chunkIndex++
		if err == io.EOF {
			return nil
		}
	}
}

// FindReaderCount counts matches without allocating result structs.
// More efficient when you only need the count.
func (TDFANestedWord) FindReaderCount(r io.Reader, cfg stream.Config) (int64, error) {
	var count int64
	err := TDFANestedWord{}.FindReader(r, cfg, func(_ stream.Match[*TDFANestedWordBytesResult]) bool {
		count++
		return true
	})
	return count, err
}

// FindReaderFirst returns the first match, or nil if none found.
// The returned result is a copy (safe to use after call returns).
func (TDFANestedWord) FindReaderFirst(r io.Reader, cfg stream.Config) (*TDFANestedWordBytesResult, int64, error) {
	var result *TDFANestedWordBytesResult
	var offset int64
	err := TDFANestedWord{}.FindReader(r, cfg, func(m stream.Match[*TDFANestedWordBytesResult]) bool {
		// Copy the result since buffer will be reused
		result = TDFANestedWord{}.copyBytesResult(m.Result)
		offset = m.StreamOffset
		return false
	})
	return result, offset, err
}

// copyBytesResult creates a deep copy of a BytesResult.
// This is needed because the original slices point into the stream buffer.
func (TDFANestedWord) copyBytesResult(src *TDFANestedWordBytesResult) *TDFANestedWordBytesResult {
	if src == nil {
		return nil
	}
	dst := &TDFANestedWordBytesResult{}
	// Copy Match slice
	dst.Match = append([]byte{}, src.Match...)
	if src.Words != nil {
		dst.Words = append([]byte{}, src.Words...)
	}
	if src.Word != nil {
		dst.Word = append([]byte{}, src.Word...)
	}
	return dst
}

// NewTransformReader returns an io.Reader that transforms matches in the input stream.
//
// The onMatch callback is called for each match. Use emit to output replacement bytes.
// If emit is not called, the match is dropped (filter behavior).
// Non-matching segments are automatically passed through to output.
//
// Example usage:
//
//	r := pattern.NewTransformReader(input, stream.DefaultTransformConfig(),
//	    func(m *PatternBytesResult, emit func([]byte)) {
//	        emit([]byte("REDACTED"))
//	    })
//	io.Copy(os.Stdout, r)
func (TDFANestedWord) NewTransformReader(r io.Reader, cfg stream.TransformConfig, onMatch func(match *TDFANestedWordBytesResult, emit func([]byte))) io.Reader {
	if cfg.BufferSize == 0 {
		cfg.BufferSize = 65536
	}
	if cfg.MaxLeftover == 0 {
		cfg.MaxLeftover = 1048576
	}

	processor := func(data []byte, isEOF bool, _ stream.TransformFunc, emitOut func([]byte)) int {
		return TDFANestedWord{}.processTransform(data, isEOF, onMatch, emitOut)
	}

	return stream.NewTransformer(r, cfg, processor, func(_ []byte, emit func([]byte)) {})
}

// processTransform processes data for transformation.
func (TDFANestedWord) processTransform(data []byte, isEOF bool, onMatch func(match *TDFANestedWordBytesResult, emit func([]byte)), emitOut func([]byte)) int {
	processed := 0
	reuseResult := &TDFANestedWordBytesResult{}

	for {
		result, ok := TDFANestedWord{}.FindBytesReuse(data[processed:], reuseResult)
		if !ok {
			// No more matches
			if isEOF {
				if processed < len(data) {
					emitOut(data[processed:])
				}
				return len(data)
			}
			// Keep potential partial match data
			safePoint := len(data) - 104857
			if safePoint < processed {
				safePoint = processed
			}
			if safePoint > processed {
				emitOut(data[processed:safePoint])
			}
			return safePoint
		}

		matchIdx := bytes.Index(data[processed:], result.Match)
		if matchIdx < 0 {
			break
		}

		matchStart := processed + matchIdx
		matchEnd := matchStart + len(result.Match)

		if matchStart > processed {
			emitOut(data[processed:matchStart])
		}

		onMatch(result, emitOut)

		if len(result.Match) > 0 {
			processed = matchEnd
		} else {
			processed++
		}
	}
	return processed
}

// ReplaceReader returns an io.Reader that replaces all matches with the template.
//
// Template syntax:
//
//	$0 or ${0}: full match
//	$1, $2, ...: capture group by index
//	$name or ${name}: capture group by name
//	$$: literal dollar sign
//
// Example:
//
//	r := pattern.ReplaceReader(input, "[$1]")
//	io.Copy(os.Stdout, r)
func (TDFANestedWord) ReplaceReader(r io.Reader, template string) io.Reader {
	parsed, err := replace.Parse(template)
	if err != nil {
		return &tDFANestedWordTransformErrReader{err: err}
	}

	captureNames := map[string]int{"words": 1, "word": 2}
	resolved, err := parsed.ValidateAndResolve(captureNames, 2)
	if err != nil {
		return &tDFANestedWordTransformErrReader{err: err}
	}

	return TDFANestedWord{}.NewTransformReader(r, stream.DefaultTransformConfig(), func(m *TDFANestedWordBytesResult, emit func([]byte)) {
		result := make([]byte, 0, len(template)+64)
		for _, seg := range resolved {
			switch seg.Type {
			case replace.SegmentLiteral:
				result = append(result, seg.Literal...)
			case replace.SegmentFullMatch:
				result = append(result, m.Match...)
			case replace.SegmentCaptureIndex:
				capture := (TDFANestedWord{}).getCaptureByIndex(m, seg.CaptureIndex)
				if capture != nil {
					result = append(result, capture...)
				}
			}
		}
		emit(result)
	})
}

// tDFANestedWordTransformErrReader is a reader that always returns an error.
type tDFANestedWordTransformErrReader struct {
	err error
}

func (r *tDFANestedWordTransformErrReader) Read(p []byte) (int, error) {
	return 0, r.err
}

// getCaptureByIndex returns the capture group value by its index.
func (TDFANestedWord) getCaptureByIndex(m *TDFANestedWordBytesResult, index int) []byte {
	switch index {
	case 0:
		return m.Match
	case 1:
		return m.Words
	case 2:
		return m.Word
	default:
		return nil
	}
}

// SelectReader returns an io.Reader that outputs only matches satisfying the predicate.
// Non-matching segments are dropped. Only matches where pred returns true are output.
//
// Example - extract all email addresses:
//
//	r := emailPattern.SelectReader(input, func(m *EmailBytesResult) bool {
//	    return true // Keep all matches
//	})
func (TDFANestedWord) SelectReader(r io.Reader, pred func(*TDFANestedWordBytesResult) bool) io.Reader {
	cfg := stream.DefaultTransformConfig()
	cfg.MaxLeftover = 1048576

	processor := func(data []byte, isEOF bool, _ stream.TransformFunc, emitOut func([]byte)) int {
		return TDFANestedWord{}.processSelect(data, isEOF, pred, emitOut)
	}

	return stream.NewTransformer(r, cfg, processor, func(_ []byte, emit func([]byte)) {})
}

// processSelect processes data for select filtering.
func (TDFANestedWord) processSelect(data []byte, isEOF bool, pred func(*TDFANestedWordBytesResult) bool, emitOut func([]byte)) int {
	processed := 0
	reuseResult := &TDFANestedWordBytesResult{}

	for {
		result, ok := TDFANestedWord{}.FindBytesReuse(data[processed:], reuseResult)
		if !ok {
			if isEOF {
				return len(data)
			}
			return processed
		}

		matchIdx := bytes.Index(data[processed:], result.Match)
		if matchIdx < 0 {
			break
		}

		matchEnd := processed + matchIdx + len(result.Match)

		if pred(result) {
			emitOut(result.Match)
		}

		if len(result.Match) > 0 {
			processed = matchEnd
		} else {
			processed++
		}
	}
	return processed
}

// RejectReader returns an io.Reader that removes matches satisfying the predicate.
// Non-matching segments pass through. Matches where pred returns true are dropped.
//
// Example - remove all sensitive data:
//
//	r := sensitivePattern.RejectReader(input, func(m *SensitiveBytesResult) bool {
//	    return true // Remove all matches
//	})
func (TDFANestedWord) RejectReader(r io.Reader, pred func(*TDFANestedWordBytesResult) bool) io.Reader {
	cfg := stream.DefaultTransformConfig()
	cfg.MaxLeftover = 1048576

	processor := func(data []byte, isEOF bool, _ stream.TransformFunc, emitOut func([]byte)) int {
		return TDFANestedWord{}.processReject(data, isEOF, pred, emitOut)
	}

	return stream.NewTransformer(r, cfg, processor, func(_ []byte, emit func([]byte)) {})
}

// processReject processes data for reject filtering.
func (TDFANestedWord) processReject(data []byte, isEOF bool, pred func(*TDFANestedWordBytesResult) bool, emitOut func([]byte)) int {
	processed := 0
	reuseResult := &TDFANestedWordBytesResult{}

	for {
		result, ok := TDFANestedWord{}.FindBytesReuse(data[processed:], reuseResult)
		if !ok {
			if isEOF {
				if processed < len(data) {
					emitOut(data[processed:])
				}
				return len(data)
			}
			safePoint := len(data) - 104857
			if safePoint < processed {
				safePoint = processed
			}
			if safePoint > processed {
				emitOut(data[processed:safePoint])
			}
			return safePoint
		}

		matchIdx := bytes.Index(data[processed:], result.Match)
		if matchIdx < 0 {
			break
		}

		matchStart := processed + matchIdx
		matchEnd := matchStart + len(result.Match)

		if matchStart > processed {
			emitOut(data[processed:matchStart])
		}

		if !pred(result) {
			emitOut(result.Match)
		}

		if len(result.Match) > 0 {
			processed = matchEnd
		} else {
			processed++
		}
	}
	return processed
}

// CaptureByIndex returns the capture group value by its 0-based index.
// Index 0 returns the full match, 1+ returns capture groups.
func (r *TDFANestedWordResult) CaptureByIndex(idx int) string {
	switch idx {
	case 0:
		return r.Match
	case 1:
		return r.Words
	case 2:
		return r.Word
	default:
		return ""
	}
}

