package curated

import (
	"fmt"
	stream "github.com/KromDaniel/regengo/stream"
	"regexp"
	"strings"
	"testing"
)

// Code generated by regengo
// Tests and benchmarks for pattern: (?P<words>(?P<word>\w+\s*)+)end

var tDFANestedWordRegexp = regexp.MustCompile("(?P<words>(?P<word>\\w+\\s*)+)end")

var tDFANestedWordTestInputs = []string{"hello world end", "a b c d e f g h i j end", "word word word word word word word word word word word word word word word word word word word word end"}

func TestTDFANestedWordMatchString(t *testing.T) {
	for _, input := range tDFANestedWordTestInputs {
		expected := tDFANestedWordRegexp.MatchString(input)
		got := TDFANestedWord{}.MatchString(input)
		if got != expected {
			t.Errorf("MatchString(%q) = %v, want %v", input, got, expected)
		}
	}
}

func TestTDFANestedWordMatchBytes(t *testing.T) {
	for _, input := range tDFANestedWordTestInputs {
		inputBytes := []byte(input)
		expected := tDFANestedWordRegexp.Match(inputBytes)
		got := TDFANestedWord{}.MatchBytes(inputBytes)
		if got != expected {
			t.Errorf("MatchBytes(%q) = %v, want %v", input, got, expected)
		}
	}
}

func TestTDFANestedWordFindString(t *testing.T) {
	for _, input := range tDFANestedWordTestInputs {
		expectedMatches := tDFANestedWordRegexp.FindStringSubmatch(input)
		result, ok := TDFANestedWord{}.FindString(input)

		if len(expectedMatches) > 0 {
			if !ok {
				t.Errorf("FindString(%q) returned false, expected match", input)
				continue
			}
			if result.Match != expectedMatches[0] {
				t.Errorf("FindString(%q).Match = %q, want %q", input, result.Match, expectedMatches[0])
			}
			{
				if result.Words != expectedMatches[1] {
					t.Errorf("FindString(%q).Words = %q, want %q", input, result.Words, expectedMatches[1])
				}
				if result.Word != expectedMatches[2] {
					t.Errorf("FindString(%q).Word = %q, want %q", input, result.Word, expectedMatches[2])
				}
			}
		} else {
			if ok {
				t.Errorf("FindString(%q) returned true, expected no match", input)
			}
		}
	}
}

func TestTDFANestedWordFindAllString(t *testing.T) {
	for _, input := range tDFANestedWordTestInputs {
		expectedAll := tDFANestedWordRegexp.FindAllStringSubmatch(input, -1)
		results := TDFANestedWord{}.FindAllString(input, -1)

		if len(results) != len(expectedAll) {
			t.Errorf("FindAllString(%q, -1) returned %d results, want %d", input, len(results), len(expectedAll))
			continue
		}
		for i := range results {
			if results[i].Match != expectedAll[i][0] {
				t.Errorf("FindAllString(%q, -1)[%d].Match = %q, want %q", input, i, results[i].Match, expectedAll[i][0])
			}
			{
				if results[i].Words != expectedAll[i][1] {
					t.Errorf("FindAllString(%q, -1)[%d].Words = %q, want %q", input, i, results[i].Words, expectedAll[i][1])
				}
				if results[i].Word != expectedAll[i][2] {
					t.Errorf("FindAllString(%q, -1)[%d].Word = %q, want %q", input, i, results[i].Word, expectedAll[i][2])
				}
			}
		}
	}
}

func BenchmarkTDFANestedWord(b *testing.B) {
	inputs := tDFANestedWordTestInputs

	b.Run("Match", func(b *testing.B) {
		for i, input := range inputs {
			input := input
			b.Run(fmt.Sprintf("Input[%d]", i), func(b *testing.B) {
				b.Run("stdlib", func(b *testing.B) {
					b.ReportAllocs()
					for b.Loop() {
						_ = tDFANestedWordRegexp.MatchString(input)
					}
				})
				b.Run("regengo", func(b *testing.B) {
					b.ReportAllocs()
					for b.Loop() {
						_ = TDFANestedWord{}.MatchString(input)
					}
				})
			})
		}
	})

	b.Run("FindFirst", func(b *testing.B) {
		for i, input := range inputs {
			input := input
			b.Run(fmt.Sprintf("Input[%d]", i), func(b *testing.B) {
				b.Run("stdlib", func(b *testing.B) {
					b.ReportAllocs()
					for b.Loop() {
						_ = tDFANestedWordRegexp.FindStringSubmatch(input)
					}
				})
				b.Run("regengo", func(b *testing.B) {
					b.ReportAllocs()
					for b.Loop() {
						_, _ = TDFANestedWord{}.FindString(input)
					}
				})
				b.Run("regengo_reuse", func(b *testing.B) {
					b.ReportAllocs()
					var result *TDFANestedWordResult
					for b.Loop() {
						result, _ = TDFANestedWord{}.FindStringReuse(input, result)
					}
				})
			})
		}
	})

	b.Run("FindAll", func(b *testing.B) {
		for i, input := range inputs {
			input := input
			b.Run(fmt.Sprintf("Input[%d]", i), func(b *testing.B) {
				b.Run("stdlib", func(b *testing.B) {
					b.ReportAllocs()
					for b.Loop() {
						_ = tDFANestedWordRegexp.FindAllStringSubmatch(input, -1)
					}
				})
				b.Run("regengo", func(b *testing.B) {
					b.ReportAllocs()
					for b.Loop() {
						_ = TDFANestedWord{}.FindAllString(input, -1)
					}
				})
				b.Run("regengo_append", func(b *testing.B) {
					b.ReportAllocs()
					results := make([]*TDFANestedWordResult, 0, 100)
					for b.Loop() {
						results = TDFANestedWord{}.FindAllStringAppend(input, -1, results[:0])
					}
				})
			})
		}
	})

}

func TestTDFANestedWordFindReader(t *testing.T) {
	for _, input := range tDFANestedWordTestInputs {
		// Get expected matches from stdlib
		expectedAll := tDFANestedWordRegexp.FindAllString(input, -1)

		// Get matches from streaming API
		var streamMatches []string
		err := TDFANestedWord{}.FindReader(strings.NewReader(input), stream.Config{}, func(m stream.Match[*TDFANestedWordBytesResult]) bool {
			streamMatches = append(streamMatches, string(m.Result.Match))
			return true
		})
		if err != nil {
			t.Errorf("FindReader(%q) error: %v", input, err)
			continue
		}

		// Compare results
		if len(streamMatches) != len(expectedAll) {
			t.Errorf("FindReader(%q) got %d matches, want %d", input, len(streamMatches), len(expectedAll))
			continue
		}
		for i := range streamMatches {
			if streamMatches[i] != expectedAll[i] {
				t.Errorf("FindReader(%q)[%d] = %q, want %q", input, i, streamMatches[i], expectedAll[i])
			}
		}
	}
}

func TestTDFANestedWordFindReaderCount(t *testing.T) {
	for _, input := range tDFANestedWordTestInputs {
		expectedAll := tDFANestedWordRegexp.FindAllString(input, -1)
		expected := int64(len(expectedAll))

		count, err := TDFANestedWord{}.FindReaderCount(strings.NewReader(input), stream.Config{})
		if err != nil {
			t.Errorf("FindReaderCount(%q) error: %v", input, err)
			continue
		}
		if count != expected {
			t.Errorf("FindReaderCount(%q) = %d, want %d", input, count, expected)
		}
	}
}

func TestTDFANestedWordFindReaderFirst(t *testing.T) {
	for _, input := range tDFANestedWordTestInputs {
		expected := tDFANestedWordRegexp.FindString(input)

		result, _, err := TDFANestedWord{}.FindReaderFirst(strings.NewReader(input), stream.Config{})
		if err != nil {
			t.Errorf("FindReaderFirst(%q) error: %v", input, err)
			continue
		}

		if expected == "" {
			if result != nil {
				t.Errorf("FindReaderFirst(%q) = %q, want no match", input, string(result.Match))
			}
		} else {
			if result == nil {
				t.Errorf("FindReaderFirst(%q) = nil, want %q", input, expected)
			} else if string(result.Match) != expected {
				t.Errorf("FindReaderFirst(%q) = %q, want %q", input, string(result.Match), expected)
			}
		}
	}
}

func TestTDFANestedWordMatchLengthInfo(t *testing.T) {
	minLen, maxLen := TDFANestedWord{}.MatchLengthInfo()
	// Just verify we get sensible values
	if minLen < 0 {
		t.Errorf("MinMatchLen = %d, expected >= 0", minLen)
	}
	if maxLen != -1 && maxLen < minLen {
		t.Errorf("MaxMatchLen = %d < MinMatchLen = %d", maxLen, minLen)
	}
}
