// Code generated by regengo
// Tests and benchmarks for pattern: (?P<outer>(?P<inner>a+)+)b

var tDFAPathologicalRegexp = regexp.MustCompile("(?P<outer>(?P<inner>a+)+)b")

var tDFAPathologicalTestInputs = []string{"aaaaaaaaaaaaaaaaaaaab", "aaaaaaaaaaaaaaaaaaaaaaaaaaaaaab", "aaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaab", "aaaaaaaaaaaaaaaaaaaaaaaaaaaaaa"}

func TestTDFAPathologicalMatchString(t *testing.T) {
	for _, input := range tDFAPathologicalTestInputs {
		expected := tDFAPathologicalRegexp.MatchString(input)
		got := TDFAPathological{}.MatchString(input)
		if got != expected {
			t.Errorf("MatchString(%q) = %v, want %v", input, got, expected)
		}
	}
}

func TestTDFAPathologicalMatchBytes(t *testing.T) {
	for _, input := range tDFAPathologicalTestInputs {
		inputBytes := []byte(input)
		expected := tDFAPathologicalRegexp.Match(inputBytes)
		got := TDFAPathological{}.MatchBytes(inputBytes)
		if got != expected {
			t.Errorf("MatchBytes(%q) = %v, want %v", input, got, expected)
		}
	}
}

func TestTDFAPathologicalFindString(t *testing.T) {
	for _, input := range tDFAPathologicalTestInputs {
		expectedMatches := tDFAPathologicalRegexp.FindStringSubmatch(input)
		result, ok := TDFAPathological{}.FindString(input)

		if len(expectedMatches) > 0 {
			if !ok {
				t.Errorf("FindString(%q) returned false, expected match", input)
				continue
			}
			if result.Match != expectedMatches[0] {
				t.Errorf("FindString(%q).Match = %q, want %q", input, result.Match, expectedMatches[0])
			}
			{
				if result.Outer != expectedMatches[1] {
					t.Errorf("FindString(%q).Outer = %q, want %q", input, result.Outer, expectedMatches[1])
				}
				if result.Inner != expectedMatches[2] {
					t.Errorf("FindString(%q).Inner = %q, want %q", input, result.Inner, expectedMatches[2])
				}
			}
		} else {
			if ok {
				t.Errorf("FindString(%q) returned true, expected no match", input)
			}
		}
	}
}

func TestTDFAPathologicalFindAllString(t *testing.T) {
	for _, input := range tDFAPathologicalTestInputs {
		expectedAll := tDFAPathologicalRegexp.FindAllStringSubmatch(input, -1)
		results := TDFAPathological{}.FindAllString(input, -1)

		if len(results) != len(expectedAll) {
			t.Errorf("FindAllString(%q, -1) returned %d results, want %d", input, len(results), len(expectedAll))
			continue
		}
		for i := range results {
			if results[i].Match != expectedAll[i][0] {
				t.Errorf("FindAllString(%q, -1)[%d].Match = %q, want %q", input, i, results[i].Match, expectedAll[i][0])
			}
			{
				if results[i].Outer != expectedAll[i][1] {
					t.Errorf("FindAllString(%q, -1)[%d].Outer = %q, want %q", input, i, results[i].Outer, expectedAll[i][1])
				}
				if results[i].Inner != expectedAll[i][2] {
					t.Errorf("FindAllString(%q, -1)[%d].Inner = %q, want %q", input, i, results[i].Inner, expectedAll[i][2])
				}
			}
		}
	}
}

