// Code generated by regengo
// Tests and benchmarks for pattern: (?P<major>\d+)\.(?P<minor>\d+)\.(?P<patch>\d+)(?:-(?P<prerelease>[\w.-]+))?(?:\+(?P<build>[\w.-]+))?

var tDFASemVerRegexp = regexp.MustCompile("(?P<major>\\d+)\\.(?P<minor>\\d+)\\.(?P<patch>\\d+)(?:-(?P<prerelease>[\\w.-]+))?(?:\\+(?P<build>[\\w.-]+))?")

var tDFASemVerTestInputs = []string{"1.0.0", "2.1.3-alpha.1", "3.0.0-beta.2+build.123", "10.20.30-rc.1+20240115"}

func TestTDFASemVerMatchString(t *testing.T) {
	for _, input := range tDFASemVerTestInputs {
		expected := tDFASemVerRegexp.MatchString(input)
		got := TDFASemVer{}.MatchString(input)
		if got != expected {
			t.Errorf("MatchString(%q) = %v, want %v", input, got, expected)
		}
	}
}

func TestTDFASemVerMatchBytes(t *testing.T) {
	for _, input := range tDFASemVerTestInputs {
		inputBytes := []byte(input)
		expected := tDFASemVerRegexp.Match(inputBytes)
		got := TDFASemVer{}.MatchBytes(inputBytes)
		if got != expected {
			t.Errorf("MatchBytes(%q) = %v, want %v", input, got, expected)
		}
	}
}

func TestTDFASemVerFindString(t *testing.T) {
	for _, input := range tDFASemVerTestInputs {
		expectedMatches := tDFASemVerRegexp.FindStringSubmatch(input)
		result, ok := TDFASemVer{}.FindString(input)

		if len(expectedMatches) > 0 {
			if !ok {
				t.Errorf("FindString(%q) returned false, expected match", input)
				continue
			}
			if result.Match != expectedMatches[0] {
				t.Errorf("FindString(%q).Match = %q, want %q", input, result.Match, expectedMatches[0])
			}
			{
				if result.Major != expectedMatches[1] {
					t.Errorf("FindString(%q).Major = %q, want %q", input, result.Major, expectedMatches[1])
				}
				if result.Minor != expectedMatches[2] {
					t.Errorf("FindString(%q).Minor = %q, want %q", input, result.Minor, expectedMatches[2])
				}
				if result.Patch != expectedMatches[3] {
					t.Errorf("FindString(%q).Patch = %q, want %q", input, result.Patch, expectedMatches[3])
				}
				if result.Prerelease != expectedMatches[4] {
					t.Errorf("FindString(%q).Prerelease = %q, want %q", input, result.Prerelease, expectedMatches[4])
				}
				if result.Build != expectedMatches[5] {
					t.Errorf("FindString(%q).Build = %q, want %q", input, result.Build, expectedMatches[5])
				}
			}
		} else {
			if ok {
				t.Errorf("FindString(%q) returned true, expected no match", input)
			}
		}
	}
}

func TestTDFASemVerFindAllString(t *testing.T) {
	for _, input := range tDFASemVerTestInputs {
		expectedAll := tDFASemVerRegexp.FindAllStringSubmatch(input, -1)
		results := TDFASemVer{}.FindAllString(input, -1)

		if len(results) != len(expectedAll) {
			t.Errorf("FindAllString(%q, -1) returned %d results, want %d", input, len(results), len(expectedAll))
			continue
		}
		for i := range results {
			if results[i].Match != expectedAll[i][0] {
				t.Errorf("FindAllString(%q, -1)[%d].Match = %q, want %q", input, i, results[i].Match, expectedAll[i][0])
			}
			{
				if results[i].Major != expectedAll[i][1] {
					t.Errorf("FindAllString(%q, -1)[%d].Major = %q, want %q", input, i, results[i].Major, expectedAll[i][1])
				}
				if results[i].Minor != expectedAll[i][2] {
					t.Errorf("FindAllString(%q, -1)[%d].Minor = %q, want %q", input, i, results[i].Minor, expectedAll[i][2])
				}
				if results[i].Patch != expectedAll[i][3] {
					t.Errorf("FindAllString(%q, -1)[%d].Patch = %q, want %q", input, i, results[i].Patch, expectedAll[i][3])
				}
				if results[i].Prerelease != expectedAll[i][4] {
					t.Errorf("FindAllString(%q, -1)[%d].Prerelease = %q, want %q", input, i, results[i].Prerelease, expectedAll[i][4])
				}
				if results[i].Build != expectedAll[i][5] {
					t.Errorf("FindAllString(%q, -1)[%d].Build = %q, want %q", input, i, results[i].Build, expectedAll[i][5])
				}
			}
		}
	}
}

